mapper.select:
		SELECT 
		
		FROM PURE_ORG
       	WHERE instr(PATH,(
       		SELECT PATH
       		FROM PURE_ORG
       		WHERE ORG_ID = #{orgId}
       	))>0
		
        	and TENANT_ID = #{tenantId}
      	
		ORDER by ORD

		SELECT 
		
		FROM PURE_ORG
		WHERE (PARENT_ID in (
             SELECT ORG_ID
             FROM PURE_USER_ORG_REF
             WHERE USER_ID = #{userId,jdbcType=VARCHAR}
       		) OR ORG_ID in (
             SELECT ORG_ID
             FROM PURE_USER_ORG_REF
             WHERE USER_ID = #{userId,jdbcType=VARCHAR}
       		))
		
        	and TENANT_ID = #{tenantId,jdbcType=VARCHAR}
      	
		ORDER by ORD

		SELECT T.ORG_ID as "orgId", T.ORG_NAME as "orgName", T.PARENT_ID as "parentId", T.PATH as "path", T.TENANT_ID as "tenantId"
       FROM PURE_ORG T, PURE_ORG T1
       where instr(T.PATH,T1.PATH)>0
             and T1.ORG_ID in(
                   SELECT ORG_ID
                   from PURE_USER_ORG_REF
                   where USER_ID = #{userId}
             )
		
        	and T.TENANT_ID = #{tenantId}
      	
		ORDER by T.ORD

mapper.insert:
		INSERT INTO PURE_ORG (
		ORG_ID, ORG_NAME, PARENT_ID, ORD, PATH
		
        	,TENANT_ID
      	) 
		VALUES (
		#{orgId}, #{orgName}, #{parentId}, #{ord}, #{path} 
		
        	,#{tenantId}
      	)

mapper.update:
		UPDATE PURE_ORG
			SET 
				ORG_NAME =  #{orgName},
				PARENT_ID = #{parentId},
				ORD = #{ord},
				PATH = #{path}
			
        		,TENANT_ID = #{tenantId}
			
			WHERE 
				ORG_ID = #{orgId}
